import axios from "axios";
import {useEffect, useState} from 'react';

export default function BadgeGenerator(){

    const [badge, setBadge] = useState('');

    useEffect(() => {
        getBadge();
    });

    

    const getBadge = () => {
        axios.get("https://loremflickr.com/200/200")
            .then(response => {
                console.log(response);
                const newBadge = response.data;
                setBadge(newBadge);
            });
    }

    const putbadge = () => {
        axios.put("https://1v74t44h9b.execute-api.us-east-1.amazonaws.com/S3Test/badgepicscontainer/imageA.jpeg", badge)
            .then(response => {
                console.log(response);
            });
    }
}
