import * as React from 'react';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import CssBaseline from '@mui/material/CssBaseline';
import Box from '@mui/material/Box';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import Container from '@mui/material/Container';
import Grid from '@mui/material/Grid';
import Paper from '@mui/material/Paper';
import Link from '@mui/material/Link';
import Chart from './Chart';
import Deposits from './Deposits';
import Button from '@mui/material/Button';
import downloadImage from './BadgeGenerator';
import axios from "axios";
import {useEffect, useState} from 'react';

function Copyright(props) {
  return (
    <Typography variant="body2" color="text.secondary" align="center" {...props}>
      {'Copyright © '}
      <Link color="inherit" href="https://mui.com/">
        Elite Managing
      </Link>{' '}
      {new Date().getFullYear()}
      {'.'}
    </Typography>
  );
}

const mdTheme = createTheme();

function DashboardContent() {

  const [badge, setBadge] = useState('');
  function getBadge(){
    axios.get("https://loremflickr.com/200/200", {responseType: "blob"})
        .then((response) => {
          console.log(response)
          var file = new File([response.data], "MyJPEG.jpg", {type: "image/jpeg"});
          window.location = URL.createObjectURL(file);
          setBadge(file);
          console.log(badge);
          //const newBadge = btoa(
          //    new Uint8Array(response.data).reduce((data, byte) => data + String.fromCharCode(byte), '')
          //)        
          //const imageObjectURL = URL.createObjectURL(file);
          //console.log(base64);
          //const newBadge = response.data;
          /*const arrayBufferBadge = response.data;
          setBadge(arrayBufferBadge);
          console.log(arrayBufferBadge)*/
        });
      }

  function putBadge(){
      axios.put("https://1v74t44h9b.execute-api.us-east-1.amazonaws.com/S3Test/badgepicscontainer/new-test.jpeg", badge)
          .then(response => {
              //console.log(newBadge);
              console.log(response);
          });
      //var image = new Image();
      //image.onload = function(){
      //    console.log(image.width); // image is loaded and we have image width 
      //}
      //image.src = base64Badge.src;
      //document.body.appendChild(image);
      //Send the file to File-system
      
      /*console.log("Sending file to S3...");
      const resp = fetch("https://1v74t44h9b.execute-api.us-east-1.amazonaws.com/S3Test/badgepicscontainer/3.jpeg", {
          method: 'PUT',
          body: fs.readFileSync("data:image/png;base64" + base64Badge),
          headers: {
            'Content-Type': 'image/jpeg',
        },
      }).catch( err => {
        console.log(err);
        return null;
      });*/

      
      /*let img = new Image();
      img.onload = function() {
          console.log(img.width); // image is loaded and we have image width
      };
      img.src = "data:image/png;base64" + base64Badge;
      console.log(img);
      setBase64Badge(img);*/
      //const newBadge = atob(base64Badge);
  }

  return (
    <ThemeProvider theme={mdTheme}>
      <Box sx={{ display: 'flex' }}>
        <CssBaseline />
        <Box
          component="main"
          sx={{
            backgroundColor: (theme) =>
              theme.palette.mode === 'light'
                ? theme.palette.grey[100]
                : theme.palette.grey[900],
            flexGrow: 1,
            height: '100vh',
            overflow: 'auto',
          }}
        >
          <Toolbar />
          <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
            <Grid container spacing={3}>
              {/* Chart */}
              <Grid item xs={12} md={8} lg={9}>
                <Paper
                  sx={{
                    p: 2,
                    display: 'flex',
                    flexDirection: 'column',
                    height: 240,
                  }}
                >
                  <Chart />
                </Paper>
              </Grid>
              {/* Recent Deposits */}
              <Grid item xs={12} md={4} lg={3}>
                <Paper
                  sx={{
                    p: 2,
                    display: 'flex',
                    flexDirection: 'column',
                    height: 240,
                  }}
                >
                  <Deposits />
                </Paper>
              </Grid>
              {/* Current Badges */}              
              <Paper sx={{ p: 2, display: 'flex', flexDirection: 'column' }}>
                      <Button onClick={() => {
                        getBadge();
                        putBadge();
                      }}>
                          Generate a New Badge
                      </Button>
              </Paper>              
            </Grid>
            <Copyright sx={{ pt: 4 }} />
          </Container>
        </Box>
      </Box>
    </ThemeProvider>
  );
}

export default function Dashboard() {
  return <DashboardContent />;
}
