import * as React from 'react';
import Button from '@mui/material/Button';
import CssBaseline from '@mui/material/CssBaseline';
import TextField from '@mui/material/TextField';
import Grid from '@mui/material/Grid';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import Container from '@mui/material/Container';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import {useState} from "react";
import axios from "axios";
import {v4 as uuid} from "uuid";

const theme = createTheme();



export default function GenerateBadge() {

    'use strict'

    const Fs = require('fs')  
    const Path = require('path')  
    const Axios = require('axios')

    async function downloadImage () {  
        const url = 'https://unsplash.com/photos/AaEQmoufHLk/download?force=true'
        const path = Path.resolve(__dirname, 'images', 'code.jpg')
        const writer = Fs.createWriteStream(path)

        const response = await Axios({
            url,
            method: 'GET',
            responseType: 'stream'
        })

        response.data.pipe(writer)

        return new Promise((resolve, reject) => {
            writer.on('finish', resolve)
            writer.on('error', reject)
        })
    }

    downloadImage() 

    const [badgeType, setBadgeType] = useState('');

    const getBase64 =
        axios.get("https://loremflickr.com/320/420", {
                responseType: 'arraybuffer'
            })
            .then(response => Buffer.from(response.data, 'binary').toString('base64'));

    const handleSubmit = (event) => {
        event.preventDefault();

        axios.put('https://1v74t44h9b.execute-api.us-east-1.amazonaws.com/S3Test/badgepicscontainer/test-pic6.jpeg', {
            jpeg: axios.get("https://loremflickr.com/320/420", { responseType:"blob" })
                    .then(function (response) {

                        var reader = new window.FileReader();
                        reader.readAsDataURL(response.data); 
                        reader.onload = function() {

                            var imageDataUrl = reader.result;
                            jpeg.setAttribute("src", imageDataUrl);
        }
    })
        })
            .then(response => {
                console.log(response)
            })
    };

    return (
        <ThemeProvider theme={theme}>
            <Container component="main" maxWidth="xs" style={{marginBottom : '13vh'}}>
                <CssBaseline />
                <Box
                    sx={{
                        marginTop: 8,
                        display: 'flex',
                        flexDirection: 'column',
                        alignItems: 'center',
                    }}
                >
                    <Typography component="h1" variant="h5">
                        Badge Generator
                        <img src='image3' alt="Girl in a jacket" width="500" height="600"></img>
                    </Typography>
                    <Box component="form" noValidate onSubmit={handleSubmit} sx={{ mt: 3 }}>
                        <Grid container spacing={2}>
                            <Grid item xs={12}>
                                <TextField
                                    required
                                    fullWidth
                                    id="badgeType"
                                    label="Input a keyword for your badge"
                                    type="input"
                                    name="badgeType"
                                    value={badgeType}
                                    onChange={event => setBadgeType(event.target.value)}
                                />
                            </Grid>
                        </Grid>
                        <Button
                            type="submit"
                            fullWidth
                            variant="contained"
                            sx={{ mt: 3, mb: 2 }}
                            style={{backgroundColor : 'mediumseagreen'}}
                        >
                            Submit
                        </Button>
                    </Box>
                </Box>
            </Container>
        </ThemeProvider>
    );
}
