import React from "react";
import {Button, Card, Segment, Dimmer, Loader, Modal, Header} from "semantic-ui-react";

export default function BusinessInfo(props) {

    const displayInfo = (props) => {
        const business = props;
        console.log(business);

        if (business !== undefined) {
            return (
                <Card>
                    <Card.Content>
                        <Card.Header>Business Name: {business.businessName.S}</Card.Header>
                        <Card.Description>Description: {business.description.S}</Card.Description>
                    </Card.Content>
                </Card>
            )
        } else {
            return (
                <div>
                    <p>There is no information to display at this time.</p>
                </div>
            )
        }
    }

    return (
        <>
            {displayInfo(props)}
        </>
    )
}
